import { useRef, useState, useEffect, useContext} from "react";
import AuthContext from "./AuthProvider";
import './Login.css';

import axios from './api/axios';
import {Link, useNavigate, useLocation} from 'react-router-dom';

// const MAIL_REGEX = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
// const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!#@$%]).{8,24}$/;
const LOGIN_URL = '/auth'

const Login = () => {
    const { setAuth } =useContext(AuthContext);

    const navigate = useNavigate();
    const location = useLocation();
    const from = location.state?.from?.pathname || "/";

    const userRef = useRef();
    const errRef = useRef();


    const [mail, setMail] = useState('');
    // const [validMail, setValidMail] = useState(false);
    // const [mailFocus, setMailFocus] = useState(false);

    const [pwd, setPwd] = useState('');
    // const [validPwd, setValidPwd] = useState(false);
    // const [pwdFocus, setPwdFocus] = useState(false);


    const [errMsg, setErrMsg] = useState('');

    useEffect(() => {
        userRef.current.focus();
    }, [])
 
    // useEffect(() => {
    //     const result = MAIL_REGEX.test(mail);
    //     console.log(result);
    //     console.log(mail);
    //     setValidMail(result);
    // }, [mail])

    // useEffect(() => {
    //     const result = PWD_REGEX.test(pwd);
    //     console.log(result);
    //     console.log(pwd);
    //     setValidPwd(pwd);
    // }, [pwd])

    useEffect(() => {
        setErrMsg('');
    }, [mail, pwd])

    const handleSubmit = async (e) => {
        e.preventDefault();
        // const v1 = MAIL_REGEX.test(mail);
        // const v2 = PWD_REGEX.test(pwd);
        // if (!v1 || !v2) {
        //     setErrMsg("Invalid Entry")
        //     return;
        // }
        try {
            const response = await axios.post(LOGIN_URL, JSON.stringify({ mail, pwd }), 
            {
                headers: {'Content-Type': 'application/json'},
                withCredentials: true
            });
            // console.log(response.data);
            // console.log(response.accessToken);
            console.log(JSON.stringify(response?.data));
            const accessToken = response?.data?.accessToken;
            const roles = response?.data?.roles;
            setAuth({mail, pwd, roles, accessToken});
            setMail('');
            setPwd('');
            navigate(from, {replace:true});
        } catch (err) {
            if (!err?.response) {
                setErrMsg('No Server Response');
            } else if (err.response?.status === 400) {
                setErrMsg('Missing Mail or Password');
            } else if (err.response?.status === 401){
                setErrMsg('Unauthorized');
            } else {
                setErrMsg('Login Failed');
            }
            errRef.current.focus();
        }
    }


    return (
       
        <section>
            <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive"> 
            {errMsg}</p>
            <h1>WELCOME</h1>
            <form onSubmit={handleSubmit}>
                    <input
                    type="text"
                    id="e-mail"
                    ref={userRef}
                    autoComplete="off"
                    onChange={(e) => setMail(e.target.value)}
                    value = {mail}
                    required
                    // aria-invalid = {validMail ? "false" : "true"}
                    // aria-describedby = "mailnote"
                    // onFocus={() => setMailFocus(true)}
                    // onBlur={() => setMailFocus(false)}
                    placeholder="E-mail Address"
                    />
                    {/* <p id="mailnote" className={mailFocus && mail && !validMail ? "instructions" : "offscreen"}>
                        <FontAwesomeIcon icon={faInfoCircle} />
                        Wrong format. <br />
                        Please enter an e-mail address. <br />
                    </p> */}

                    <input
                        type="password"
                        id= "password"
                        onChange = {(e) => setPwd(e.target.value)}
                        value = {pwd}
                        required
                        // aria-invalid = {validPwd ? "false" : "true"}
                        // aria-describedby = "pwdnote"
                        // onFocus={() => setPwdFocus(true)}
                        // onBlur={() => setPwdFocus(false)}
                        placeholder="Password"
                    />
                    {/* <p id="pwdnote" className={pwdFocus && !validPwd ? "instructions" : "offscreen"}>
                        <FontAwesomeIcon icon={faInfoCircle} />
                        8 to 24 characters. <br />
                        Must include uppercase and lowercase letters, a number and a special character. <br />
                    </p>     */}

                    <Link to="/list">
                        <button id="L" /*disabled={!validMail || !validPwd ? true : false}*/>
                        Connect
                        </button>
                    </Link>
            </form>
            <p>
                Don't have an account yet ? <br />
                <span className="line">
                    <Link to="/register">Register</Link>
                </span>
            </p>
        </section>
    )
}

export default Login
