import React, { useState } from "react";
import JsonData from "./MockData.json";
import ReactPaginate from "react-paginate";
import axios from './api/axios';
import {Link} from 'react-router-dom';

function UserList() {
    const [users, setUsers] = useState(JsonData.slice(0, 50));
    const [pageNumber, setPageNumber] = useState(0);
  
    const usersPerPage = 10;
    const pagesVisited = pageNumber * usersPerPage;
  
    const displayUsers = users
      .slice(pagesVisited, pagesVisited + usersPerPage)
      .map((user) => {
        return (<tbody>
                <tr>
                    <td>{user.firstName}</td>
                    <td>{user.lastName}</td>
                    <td>{user.email}</td>
                </tr>
                </tbody>
        );
      });
  
    const pageCount = Math.ceil(users.length / usersPerPage);
  
    const changePage = ({ selected }) => {
      setPageNumber(selected);
    };

    const [currUser, setCurrent] = useState('');

    const greetUser = async (e) => {
      e.preventDefault();
      try {
        const response = await axios.post('/', JSON.stringify({mailAddress: mail, password: pwd }),
        {
            headers: {'Content-Type': 'application/json'},
            withCredentials: false
        }).then((result) => {
          if(result.data.message == 'Success'){
              console.log(result.data.name); 
              return setCurrent(result.data.name);
          }
      });
      } catch (err) {
        console.log("error!");
      }
    }
  
    return (
      <>
      <header>
        <h1>Hello {greetUser}</h1>
      </header>
      <div className="sidenav">
        <p>List of Users</p>
        <Link to="/">
          <button className="out">
            Logout
          </button>
        </Link>
      </div>
      <div className="Fond">
        <table>
          <thead>
            <tr>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Email</th>
            </tr>
            </thead>
        {displayUsers}
        </table>
        <ReactPaginate
          previousLabel={"Previous"}
          nextLabel={"Next"}
          pageCount={pageCount}
          onPageChange={changePage}
          containerClassName={"paginationBttns"}
          previousLinkClassName={"previousBttn"}
          nextLinkClassName={"nextBttn"}
          disabledClassName={"paginationDisabled"}
          activeClassName={"paginationActive"}
        />
      </div>
      </>
    );
  }
  
  export default UserList;
